"""Временный скрипт для обновления base64 для розовых гвоздик (25 шт)"""

import re

# Новый полный base64
new_base64 = "data:image/jpeg;base64,"

# Читаем файл
with open("upload_carnations_manual.py", "r", encoding="utf-8") as f:
    content = f.read()

# Заменяем None на полный base64 для розовых гвоздик (25 шт)
pattern = r"('Розовые гвоздики \(25 шт\)':\s*)(None)(\s*#.*)"
replacement = r"\1" + new_base64 + r"\3"

new_content = re.sub(pattern, replacement, content)

# Записываем обратно
with open("upload_carnations_manual.py", "w", encoding="utf-8") as f:
    f.write(new_content)

print("✅ Base64 обновлен для 'Розовые гвоздики (25 шт)'")
